import Qs from "qs";
import { removeUserinfo } from "@/utils/auth"; // get token from cookie

export default {
  name: "taskInfo",
  components: {},
  props: {
    isShowDialog: Boolean
  },
  watch: {},
  data: function data() {
    return {
      form: {
        oldPwd: "",
        pwd: "",
        comfirPwd: ""
      }
    };
  },
  computed: {},
  created: function created() {},
  mounted: function mounted() {},
  methods: {
    submit: function submit() {
      var _this = this;

      if (this.form.oldPwd.length == 0 || this.form.pwd.length == 0 || this.form.comfirPwd.length == 0) return this.$message.error("请输入完整信息");
      if (this.form.oldPwd == this.form.pwd) return this.$message.error("新密码和旧密码不能一样");
      if (this.form.pwd != this.form.comfirPwd) return this.$message.error("两次输入密码不一样,请重新输入");
      this.axios.put(this.urls.changepwd + "?" + Qs.stringify(this.form)).then(function (response) {
        if (response.code == 0) return _this.$message.error(response.msg);

        _this.$message({
          message: "密码修改成功,请重新登录",
          type: "success"
        });

        _this.$emit("loginOut");
      });
    },
    //dialog关闭按钮
    handleClose: function handleClose() {
      this.$emit("editDialog", false);
    }
  }
};
