            _this2.$store.commit("userinfo", response.data); // if (response.data.errcode == 0)
            //   return this.$message.error(response.msg);


            _this2.$store.dispatch("user/login", {
              username: "admin",
              password: "111111"
            }).then(function () {
              _this2.$router.push({
                path: _this2.redirect || "/"
              });

              _this2.loading = false;
            }).catch(function () {
              _this2.loading = false;
            });
          }).catch(function () {
            _this2.loading = false;
          });
        } else {
          console.log("error submit!!");
          return false;
        }
      });
    }
  }
};
