import { getTimeDate } from "@/utils/index.js";

import Qs from "qs";
// 1，未完善资料，2、已提交资料，待平台审核，3、审核中，4、审核通过，5、审核失败，6、正常，7、锁定，8，黑名单
export default {
  data() {
    return {
      list: null,
      listLoading: false,
      form: {
        bankType: "",
        bankCardNo: "",
        bankUserName: "",

        bankName: "",
        subBranchName: "",
        id: ""
      },
      rules: {
        bankType: [
          { required: true, trigger: "blur", message: "请选择账号类型" }
        ],
        bankCardNo: [
          { required: true, trigger: "blur", message: "请输入账号" }
        ],
        bankUserName: [
          { required: true, trigger: "blur", message: "请输入收款人姓名" }
        ],
        bankName: [
          { required: true, trigger: "blur", message: "请输入银行名称" }
        ],
        subBranchName: [
          { required: true, trigger: "blur", message: "请输入开户行" }
        ]
      }
    };
  },
  created() {},
  computed: {},
  mounted() {
    this.fetchData();
  },
  methods: {
    statusFilter(status) {
      const statusMap = {
        1: "银行卡",
        2: "支付宝",
        3: "微信"
      };
      return statusMap[status];
    },
    formatTime(row, column, cellValue) {
      return getTimeDate(cellValue);
    },
    submitForm(formName) {
      this.$refs[formName].validate(valid => {
        if (valid) {
          this.cardadd();
        } else {
          console.log("error submit!!");
          return false;
        }
      });
    },
    resetForm(formName) {
      this.$refs[formName].resetFields();
    },

    //获取收款卡列表
    fetchData() {
      let param = {
        bankname: "",
        bankUserName: "",
        _uiName_: "eleme",
        _pagination: "{'pageNumber':1,'pageSize':10}"
      };

      this.listLoading = true;
      this.axios
        .get(this.urls.banklist + "?" + Qs.stringify(param))
        .then(response => {
          this.list = response.data.list;
          this.listLoading = false;
        });
    },

    //删除收款卡
    carddel(index, row) {
      console.log(index, row);
      this.$confirm("是否确认删除?", "提示", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        type: "warning"
      }).then(() => {
        let param = {
          id: row.id
        };
        this.axios
          .delete(this.urls.carddel + "?" + Qs.stringify(param))
          .then(response => {
            this.fetchData();
          });
      });
    },
    //新增收款卡
    cardadd() {
      let param = {
        bankName: this.form.bankName,
        subBranchName: this.form.subBranchName,
        bankCardNo: this.form.bankCardNo,
        bankUserName: this.form.bankUserName,
        bankType: this.form.bankType,
        isPayAccount: "1"
      };
      this.axios
        .post(this.urls.cardadd + "?" + Qs.stringify(param))
        .then(response => {
          this.$message.success("新增成功");
          this.fetchData();
        });
    },
    setdefault(index, row) {
      let param = {
        isPayAccount: "1",
        id: row.id
      };
      this.axios
        .post(this.urls.cardadd + "?" + Qs.stringify(param))
        .then(response => {
          this.$message.success("操作成功");
          this.fetchData();
        });
    }
  }
};
