import CountTo from "vue-count-to";
import Qs from "qs";

export default {
  components: {
    CountTo
  },
  data() {
    return {
      //所有订单
      totalOrders: 0,
      //所有商家
      totalBusiness: 0,
      //所有用户
      totalUsers: 0,
      //所有充值
      totalRecharge: 0
    };
  },
  computed: {
    getbalance() {
      return 0;
    }
  },
  mounted() {
    this.getTodayOrders();

    this.getTodayBusiness();

    this.getTodayUser();

    this.getTodayRecharge();
  },
  methods: {
    //获取当前时间，格式YYYY-MM-DD
    getNowFormatDate() {
      var date = new Date();
      var seperator1 = "-";
      var year = date.getFullYear();
      var month = date.getMonth() + 1;
      var strDate = date.getDate();
      if (month >= 1 && month <= 9) {
        month = "0" + month;
      }
      if (strDate >= 0 && strDate <= 9) {
        strDate = "0" + strDate;
      }
      var currentdate = year + seperator1 + month + seperator1 + strDate;
      return currentdate;
    },
    getTomorrowFormatDate() {
      var date = new Date();
      var seperator1 = "-";
      var year = date.getFullYear();
      var month = date.getMonth() + 1;
      var strDate = date.getDate() + 1;
      if (month >= 1 && month <= 9) {
        month = "0" + month;
      }
      if (strDate >= 0 && strDate <= 9) {
        strDate = "0" + strDate;
      }
      var currentdate = year + seperator1 + month + seperator1 + strDate;
      return currentdate;
    },

    handleSetLineChartData(type) {
      this.$router.push(type);
    },

    //今日订单
    getTodayOrders() {
      let param = {
        progress: "",
        name: "",
        starttime: this.getNowFormatDate(),
        endtime: this.getTomorrowFormatDate(),
        desc: "2",
        _uiName_: "eleme",
        _pagination: "{'pageNumber':1,'pageSize':10}"
      };
      this.axios
        .get(this.urls.allorderlist + "?" + Qs.stringify(param))
        .then(response => {
          this.totalOrders = response.data.totalRow;
        });
    },
    //今日商家
    getTodayBusiness() {
      let param = {
        businessStatusId: "",
        starttime: this.getNowFormatDate(),
        endtime: this.getTomorrowFormatDate(),
        _uiName_: "eleme",
        _pagination: "{'pageNumber':1,'pageSize':10}"
      };
      this.axios
        .get(this.urls.getbusinesslist + "?" + Qs.stringify(param))
        .then(response => {
          this.totalBusiness = response.data.totalRow;
        });
    },
    //今日用户
    getTodayUser() {
      let param = {
        userStatusId: "",
        starttime: this.getNowFormatDate(),
        endtime: this.getTomorrowFormatDate(),
        _uiName_: "eleme",
        _pagination: "{'pageNumber':1,'pageSize':10}"
      };
      this.axios
        .get(this.urls.getuserlist + "?" + Qs.stringify(param))
        .then(response => {
          this.totalUsers = response.data.totalRow;
        });
    },

    //今日充值金额
    getTodayRecharge() {
      let param = {
        starttime: this.getNowFormatDate(),
        endtime: this.getTomorrowFormatDate(),
        shopType: "",
        shopAccount: "",
        shopName: "",
        shopUrl: "",
        _uiName_: "eleme",
        _pagination: "{'pageNumber':1,'pageSize':10}"
      };
      this.axios
        .get(this.urls.rechargelist + "?" + Qs.stringify(param))
        .then(response => {
          let total = 0;
          response.data.list.forEach(row => {
            if (row.statusName == "充值成功") {
              total += row.money / 1;
            }
          });
          this.totalRecharge = total;
        });
    }
  }
};
