import Qs from "qs";
import { getTimeDate } from "@/utils/index.js";
import UserInfo from "./infoDialog";

export default {
  components: { UserInfo },

  filters: {
    statusFilter(status) {
      const statusMap = {
        未使用: "success",
        已使用: "gray"
      };
      return statusMap[status];
    }
  },
  data() {
    return {
      currentPage: 1,
      pageSize: 10,
      total: 0,

      dialogTableVisible: false,
      selectTaskData: {},
      list: [],
      listLoading: true,
      form: {
        num: "",
        status: "未使用"
      },

      rules: {
        userId: [{ required: true, trigger: "blur", message: "请输入账号ID" }]
      }
    };
  },
  created() {
    this.fetchData();
  },
  methods: {
    formatTime(row, column, cellValue) {
      return getTimeDate(cellValue);
    },
    submitForm(formName) {
      this.$prompt("请输入生成数量", "提示", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        inputPattern: /^\+?[1-9]\d*$/,
        inputErrorMessage: "请输入数字"
      })
        .then(({ value }) => {
          let param = {
            num: value
          };
          this.axios
            .post(this.urls.addinvitation + "?" + Qs.stringify(param))
            .then(response => {
              this.fetchData();
            });
        })
        .catch(() => {});
    },

    fetchData() {
      this.listLoading = true;

      let page = { pageNumber: this.currentPage, pageSize: this.pageSize };

      let param = {
        status: this.form.status,
        _uiName_: "eleme",
        _pagination: JSON.stringify(page)
      };
      this.axios
        .get(this.urls.invitationlist + "?" + Qs.stringify(param))
        .then(response => {
          this.list = response.data.list;

          this.total = response.data.totalRow;

          this.listLoading = false;
        });
    },
    handleSizeChange(val) {
      this.pageSize = val;
      this.fetchData();
    },
    handleCurrentChange(val) {
      this.currentPage = val;
      this.fetchData();
    },

    //查询
    lookinfo(row, index) {
      this.dialogTableVisible = true;
      this.selectTaskData = row;
    },
    editDialogListener(bol) {
      this.dialogTableVisible = bol;
    }
  }
};
