import { getTimeDate } from "@/utils/index.js";
import {
  yanhaologin,
  yanhaoPush,
  yanhaoQuery,
  yanhaoBoxQuery
} from "@/utils/validate.js";

import UserInfo from "./userinfodialog";
import Wangwang from "./wangwanginfo";

import Qs from "qs";
// 1，未完善资料，2、已提交资料，待平台审核，3、审核中，4、审核通过，5、审核失败，6、正常，7、锁定，8，黑名单
export default {
  components: { UserInfo, Wangwang },

  filters: {
    statusFilter(status) {
      const statusMap = {
        1: "info",
        2: "gray",
        3: "gray",
        4: "success",
        5: "danger",
        6: "success",
        7: "danger",
        8: "danger"
      };
      return statusMap[status];
    }
  },
  data() {
    return {
      currentPage: 1,
      pageSize: 10,
      total: 0,

      wangwanginfoVisible: false,

      dialogTableVisible: false,
      selectWangwangData: {},

      selectTaskData: {},

      list: null,
      listLoading: true,
      form: {
        userStatusId: "",
        starttime: "",
        endtime: "",
        _uiName_: "eleme"
      }
    };
  },
  created() {},
  computed: {},
  mounted() {
    this.fetchData();
  },
  methods: {
    formatTime(row, column, cellValue) {
      return getTimeDate(cellValue);
    },
    resetForm(formName) {
      this.form.userStatusId = "";
      this.form.starttime = this.form.endtime = "";
    },

    //获取用户列表
    fetchData() {
      let page = { pageNumber: this.currentPage, pageSize: this.pageSize };

      this.form["_pagination"] = JSON.stringify(page);
      this.listLoading = true;
      this.axios
        .get(this.urls.getuserlist + "?" + Qs.stringify(this.form))
        .then(response => {
          this.list = response.data.list;
          this.listLoading = false;
          this.total = response.data.totalRow;

          this.list.forEach(item => {
            item.loading = false;
          });
        });
    },
    operationHandle(index, row, type) {
      let param = {
        id: row.id,
        userStatusId: type
      };
      switch (type) {
        case 5:
          this.operationRemote(this.urls.editiusernfo, param); //拒绝
          break;
        case 6:
          this.operationRemote(this.urls.editiusernfo, param); //通过
          break;
        case 7:
          this.operationRemote(this.urls.editiusernfo, param); //拉黑
          break;
        case 9:
          // this.operationRemote(this.urls.ordersuccress, param); //查看资料
          break;
        case 2:
          this.operationRemote(this.urls.orderlist, param); //详情 TODO
          break;
        default:
          break;
      }
    },

    operationRemote(url, param) {
      this.axios.put(url + "?" + Qs.stringify(param)).then(response => {
        this.fetchData();
      });
    },

    //查询用户详情
    lookinfo(index, row) {
      this.dialogTableVisible = true;
      this.selectTaskData = row;
    },
    editDialogListener(bol) {
      this.dialogTableVisible = bol;
    },

    //查询旺旺详情
    wangwanginfo(index, row, taobaoname) {
      //先从系统库中查询旺旺号;
      let param = {
        userId: taobaoname
      };
      let _that = this;
      this.axios
        .get(this.urls.taobaoquery + "?" + Qs.stringify(param))
        .then(async response => {
          //如果查询到结果
          if (response.data) {
            this.wangwanginfoVisible = true;
            this.selectWangwangData = response.data;
          } else {
            row.loading = true;
            _that.$set(_that.list, index, row);
            this.$message.warning(
              "当前系统无该用户记录,正在远程查询,请15秒后再试。"
            );
            //如果未查询到结果,先推送到查询队列
            let res = await yanhaoPush(taobaoname);
            if (res == 0) {
              row.loading = false;
              return this.$message.error("验号账户登录过期，请退出重新登录。");
            }

            //15秒之后查询
            setTimeout(function() {
              row.loading = false;
              _that.$set(_that.list, index, row);
              //15秒后查询结果
              yanhaoQuery();
            }, 15000);
          }
        });
    },
    //更新旺旺详情
    async updatewangwanginfo(index, row, taobaoname) {
      let _that = this;
      row.loading = true;
      _that.$set(_that.list, index, row);
      this.$message.warning("正在更新,请15秒后再试。");
      //如果未查询到结果,先推送到查询队列
      let res = await yanhaoPush(taobaoname);
      if (res == 0) {
        row.loading = false;
        return this.$message.error("验号账户登录过期，请退出重新登录。");
      }

      //15秒之后查询
      setTimeout(function() {
        row.loading = false;
        _that.$set(_that.list, index, row);
        //15秒后查询结果
        yanhaoQuery();
      }, 15000);
    },

    //魔盒验号
    async boxCheck(index, row, taobaoname) {
      let _that = this;

      setTimeout(function() {
        row.loading = false;
        _that.$set(_that.list, index, row);
        //15秒后查询结果
        yanhaoBoxQuery(taobaoname);
      }, 1000);
    },

    wangwangListener(bol) {
      this.wangwanginfoVisible = bol;
    },

    handleSizeChange(val) {
      this.pageSize = val;
      this.fetchData();
    },
    handleCurrentChange(val) {
      this.currentPage = val;
      this.fetchData();
    }
  }
};
