import { getTimeDate } from "@/utils/index.js";
import Noticeinfo from "./noticeinfo";

import Qs from "qs";
// 1，未完善资料，2、已提交资料，待平台审核，3、审核中，4、审核通过，5、审核失败，6、正常，7、锁定，8，黑名单
export default {
  components: { Noticeinfo },

  filters: {
    statusFilter(status) {
      const statusMap = {
        1: "gray",
        2: "gray",
        3: "danger",
        4: "success",
        5: "danger",
        6: "success",
        7: "danger",
        8: "danger"
      };
      return statusMap[status];
    }
  },
  data() {
    return {
      infoVisible: false,

      selectData: "",

      shoptypelist: [],
      list: null,
      listLoading: true,
      form: {
        starttime: "",
        endtime: "",
        _uiName_: "eleme",
        _pagination: "{'pageNumber':1,'pageSize':10}"
      }
    };
  },
  created() {},
  computed: {},
  mounted() {
    this.fetchData();
  },
  methods: {
    formatTime(row, column, cellValue) {
      return getTimeDate(cellValue);
    },

    addNotice() {
      this.selectData = "add";
      this.infoVisible = true;
    },
    lookinfo(row) {
      this.selectData = row.memo;
      this.infoVisible = true;
    },

    fetchData() {
      this.listLoading = true;
      this.axios
        .get(this.urls.noticelist + "?" + Qs.stringify(this.form))
        .then(response => {
          this.list = response.data.list;
          this.listLoading = false;
        });
    },

    //删除
    delede(index, row) {
      console.log(index, row);
      this.$confirm("是否确认删除该公告?", "提示", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        type: "warning"
      }).then(() => {
        let param = {
          id: row.id
        };
        this.axios
          .delete(this.urls.deletenotice + "?" + Qs.stringify(param))
          .then(response => {
            this.fetchData();
          });
      });
    },

    wangwangListener(bol) {
      this.infoVisible = bol;
    }
  }
};
