import { getTimeDate } from "@/utils/index.js";

import Qs from "qs";
// 1，未完善资料，2、已提交资料，待平台审核，3、审核中，4、审核通过，5、审核失败，6、正常，7、锁定，8，黑名单
export default {
  filters: {
    statusFilter(status) {
      const statusMap = {
        1: "gray",
        2: "gray",
        3: "danger",
        4: "success",
        5: "danger",
        6: "success",
        7: "danger",
        8: "danger"
      };
      return statusMap[status];
    }
  },
  data() {
    return {
      shoptypelist: [],
      list: null,
      listLoading: true,
      form: {
        shopType: "",
        shopAccount: "",
        shopName: "",
        shopUrl: ""
      }
    };
  },
  created() {
    //获取店铺类型
    this.axios.get(this.urls.shoptypelist).then(response => {
      this.shoptypelist = response.data;
    });
  },
  computed: {},
  mounted() {
    this.fetchData();
  },
  methods: {
    getShoptype(row, column, cellValue) {
      let label = "未知";
      this.shoptypelist.forEach(p => {
        if (p.id == cellValue) {
          label = p.name;
        }
      });
      return label;
    },
    formatTime(row, column, cellValue) {
      return getTimeDate(cellValue);
    },

    resetForm(formName) {
      this.$refs[formName].resetFields();
    },

    //获取店铺列表
    fetchData() {
      console.log(JSON.stringify(this.$store.state.userinfo));
      let param = {
        businessId: this.$store.state.userinfo.id,
        name: "",
        _uiName_: "eleme",
        _pagination: "{'pageNumber':1,'pageSize':10}"
      };
      this.listLoading = true;
      this.axios
        .get(this.urls.shoplist + "?" + Qs.stringify(param))
        .then(response => {
          this.list = response.data.list;
          this.listLoading = false;
        });
    },

    //删除店铺
    deleteShop(index, row) {
      console.log(index, row);
      this.$confirm("是否确认删除该店铺?", "提示", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        type: "warning"
      }).then(() => {
        let param = {
          id: row.id
        };
        this.axios
          .delete(this.urls.delshop + "?" + Qs.stringify(param))
          .then(response => {
            this.fetchData();
          });
      });
    }
  }
};
