import Qs from "qs";

export default {
  name: "taskInfo",
  components: {},
  props: {
    isShowDialog: Boolean,
    taskData: { type: String }
  },
  watch: {
    taskData: function(newValue, oldValue) {
      if (newValue == "add") {
        this.look = false;
        this.textarea = "";
      } else {
        this.textarea = newValue;
        this.look = true;
      }
    }
  },
  data() {
    return {
      textarea: "",
      type: ""
    };
  },
  computed: {
    getbalance() {
      let userinfo = this.$store.state.userinfo;
      return userinfo.balance;
    }
  },
  created() {},
  mounted() {},
  methods: {
    addNotice() {
      let param = {
        memo: this.textarea,
        type: this.type
      };
      this.axios
        .post(this.urls.addnotice + "?" + Qs.stringify(param))
        .then(response => {
          this.handleClose();
        });
    },
    //dialog关闭按钮
    handleClose() {
      this.$emit("editDialog", false);
    }
  }
};
